INSERT INTO `projects` (`id`, `title`, `category`, `short_description`, `full_description`, `image_path`, `gallery_images`, `technologies_used`, `challenges`, `solutions`, `project_date`, `created_at`, `updated_at`, `status`, `completion_date`, `video_paths`, `youtube_videos`) VALUES
(1, 'Interactive Terminal Website', 'Web Development', 'A unique personal website designed as an interactive terminal, featuring games, dynamic themes, and various commands.', '<section class=\"project-header\"><br>\r\nA web-based terminal interface that simulates a command-line environment, featuring interactive games, customizable themes, and dynamic background effects. The project demonstrates modern web technologies while maintaining the classic terminal aesthetic.<br><br><br>\r\n\r\n<h2>Key Features:</h2><br>\r\n\r\n&nbsp â€¢ Command System: Implements a robust command system with history tracking, allowing users to navigate through previous commands using arrow keys.<br>\r\n\r\n&nbsp â€¢ Interactive Games: Features multiple games including Hangman with 30 programming-related words and a Number Guessing game with three difficulty levels.<br>\r\n\r\n&nbsp â€¢ Theme Customization: Offers multiple theme options including Default, Cyberpunk, Retro, Matrix, and more, with persistent settings using localStorage.<br>\r\n\r\n&nbsp â€¢ Background Effects: Dynamic background effects using Canvas API, including the iconic Matrix rain effect.<br>\r\n\r\n&nbsp â€¢ Version Management: Sophisticated version control system with automatic changelog generation and cache-busting functionality.<br><br>\r\n\r\n<h2>Technical Implementation:</h2><br>\r\n\r\n&nbsp â€¢ Built with vanilla JavaScript for optimal performance<br>\r\n&nbsp â€¢ Modular architecture with separate modules for commands, games, and effects<br>\r\n&nbsp â€¢ Responsive design across all screen sizes<br>\r\n&nbsp â€¢ Automatic cache-busting system for seamless updates<br>\r\n&nbsp â€¢ Detailed changelog and version tracking system<br><br>\r\n\r\nThe project demonstrates the possibility of creating engaging, interactive experiences while maintaining the simplicity and efficiency of a terminal interface.<br><br>\r\n\r\n<h2>Project Links:</h2><br>\r\n\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Live Demo: <a href=\"https://salah-projects.free.nf/project1/\" target=\"_blank\">View Demo</a><br>\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ GitHub Repository: Not Available<br>\r\n<br><br>\r\n\r\n<p class=\"date\">This project is a proprietary development showcasing interactive terminal-based web applications. Currently available for demo purposes only.</p>\r\n</section>', 'Images/projects/MainPreview.jpg', '[\"Images\\/gallery\\/67a0d3f7add74.png\",\"Images\\/gallery\\/Iphone-PreviewP1.jpg\",\"Images\\/gallery\\/phone.png\"]', 'Vanila JavaScript, Html5, Css3', '- Implementing a realistic terminal interface with command history<br>\r\n- Creating an efficient version management system<br>\r\n- Handling browser caching for updates<br>\r\n- Developing interactive games within the terminal interface<br>\r\n- Managing multiple theme systems with smooth transitions<br>', '- Developed a custom command system with history tracking<br>\r\n- Implemented VERSION.md and CHANGELOG.md with automatic generation scripts<br>\r\n- Added cache-busting query parameters with automatic updates<br>\r\n- Created modular game systems with state management<br>\r\n- Built a theme system with localStorage persistence<br>', '2025-02-02', '2025-02-07 22:39:23', '2025-03-27 02:56:24', 'On Hold', NULL, '[]', '[]'),
(2, '2D Platformer Adventure', 'Game Development', 'A challenging platformer game built with Godot Engine, featuring precise controls, atmospheric pixel art design, and progressively challenging levels.', '<section class=\"project-header\"><br>A challenging 2D platformer game developed using the Godot Engine, featuring precise platforming mechanics and atmospheric level design. The game combines classic platforming elements with modern game design principles to create an engaging and challenging experience.<br><br><br>\r\n\r\n<h2>Key Features:</h2><br>\r\n\r\n&nbsp â€¢ Player Movement: Implements smooth and responsive character controls with precise jumping mechanics<br>\r\n\r\n&nbsp â€¢ Level Design: Features carefully crafted levels with increasing difficulty and strategic platform placement<br>\r\n\r\n&nbsp â€¢ Visual Style: Utilizes pixel art aesthetics with attention to environmental detail and visual coherence<br>\r\n\r\n&nbsp â€¢ Audio System: Integrated sound effects and background music to enhance gameplay immersion<br>\r\n\r\n&nbsp â€¢ Collectible System: Implements various collectible items throughout levels for added gameplay depth<br>\r\n\r\n&nbsp â€¢ Health System: Features life management with visual health indicators<br><br>\r\n\r\n<h2>Technical Implementation:</h2><br>\r\n\r\n&nbsp â€¢ Built with Godot Engine 4.3 for optimal performance<br>\r\n&nbsp â€¢ Custom physics-based movement system<br>\r\n&nbsp â€¢ Modular level design architecture<br>\r\n&nbsp â€¢ State management system for player actions<br>\r\n&nbsp â€¢ Integrated animation system for smooth character movement<br>\r\n&nbsp â€¢ Collision detection and response handling<br><br>\r\n\r\nThe project showcases the potential of modern game development tools while maintaining the charm and challenge of classic platformer games.<br><br>\r\n\r\n<h2>Project Links:</h2><br>\r\n\r\n\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Engine: <a href=\"https://godotengine.org\">Godot 4.3</a><br>\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Beta Test: In Progress<br>\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Platform: PC (Windows/Linux)<br><br>\r\n\r\n<p class=\"date\">This project is currently under active development, with new features and levels being added regularly. Stay tuned for updates and the upcoming release.</p>', 'Images/projects/Screenshot 2025-02-07 212519.png', '[\"Images\\/gallery\\/Screenshot 2025-02-07 212545.png\",\"Images\\/gallery\\/Screenshot 2025-02-07 212614.png\",\"Images\\/gallery\\/Screenshot 2025-02-07 212654.png\"]', 'Godot Engine, GDScript, Aseprite', '- Implementing precise platforming physics and responsive controls<br>\r\n- Creating engaging level design with balanced difficulty progression<br>\r\n- Optimizing game performance and animation systems<br>\r\n- Developing reliable checkpoint and save state systems<br>\r\n- Managing collision detection and player movement mechanics<br>', '- Developed custom character controller with precise movement and physics<br>\r\n- Implemented modular level design system with progressive difficulty<br>\r\n- Optimized sprite animations and game performance through efficient coding<br>\r\n- Built robust save/load system with checkpoint functionality<br>\r\n- Created responsive collision and movement system with smooth controls<br>', '2024-11-08', '2025-02-07 22:45:28', '2025-02-12 01:45:34', 'On Hold', NULL, NULL, '[\"28gzr2MMPfQ\",\"3WOf-T2V7gs\"]'),
(4, 'XO Game', 'Game Development', 'A modern Tic-tac-toe implementation featuring AI opponents, multiple themes, and comprehensive statistics tracking, built with JavaFX.', '<section class=\"project-header\">\r\nA modern implementation of the classic Tic-tac-toe game developed using JavaFX, featuring an intelligent AI opponent, customizable themes, and a comprehensive statistics system. This project combines traditional gameplay with modern design principles and user experience features.\r\n<br><br>\r\n\r\n<h2>Key Features:</h2><br>\r\n\r\n&nbsp â€¢ AI Opponent: Implements multiple difficulty levels for challenging gameplay<br>\r\n&nbsp â€¢ Theme System: Features four distinct visual themes (Classic, Dark, Light, Neon)<br>\r\n&nbsp â€¢ Sound System: Integrated background music and game effect sounds with volume controls<br>\r\n&nbsp â€¢ Statistics Tracking: Comprehensive game statistics including wins, losses, and draw rates<br>\r\n&nbsp â€¢ Responsive UI: Clean and intuitive user interface with smooth transitions<br>\r\n&nbsp â€¢ Settings Management: Persistent user preferences and game statistics<br><br>\r\n\r\n<h2>Technical Implementation:</h2><br>\r\n\r\n&nbsp â€¢ Built with JavaFX 21.0.2 for modern UI capabilities<br>\r\n&nbsp â€¢ Modular architecture using FXML for view separation<br>\r\n&nbsp â€¢ Custom theme management system with real-time theme switching<br>\r\n&nbsp â€¢ Persistent data storage using Java Preferences API<br>\r\n&nbsp â€¢ Scene graph-based UI with CSS styling<br>\r\n&nbsp â€¢ Maven-based build system with custom plugins<br><br>\r\n\r\nThe project demonstrates modern Java application development practices while providing an engaging gaming experience with additional features beyond the basic game mechanics.<br><br>\r\n\r\n<h2>Project Links:</h2><br>\r\n\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Technology: <a href=\"https://openjfx.io/\">JavaFX 21.0.2</a><br>\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Download: <a href=\"https://github.com/SallahBoussettah/XO-Game/releases/download/V1.0.0/XO-Game.exe\">XO Game Installer</a><br> \r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Platform: Windows<br>\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Version: 1.0.0<br><br>\r\n\r\n<p class=\"date\">A complete desktop application showcasing modern Java development practices with a focus on user experience and visual customization.</p>\r\n</section>', 'Images/projects/image_2025-02-15_010340348.png', '[\"Images\\/gallery\\/Screenshot 2025-02-15 003908.png\",\"Images\\/gallery\\/Screenshot 2025-02-15 003935.png\",\"Images\\/gallery\\/Screenshot 2025-02-15 003959.png\",\"Images\\/gallery\\/Screenshot 2025-02-15 004022.png\"]', 'Java 21, JavaFX, Maven, FXML, CSS, JPackage, Java Preferences API', '- Implementing an intelligent AI system that provides varying difficulty levels<br>\r\n- Designing a flexible theme system that can switch in real-time<br>\r\n- Managing game state and user preferences across different scenes<br>\r\n- Creating a responsive and intuitive user interface<br>\r\n- Implementing a comprehensive audio system<br><br>', '- Developed a minimax algorithm with difficulty-based decision making<br>\r\n- Created a modular theme architecture using CSS and dynamic styling<br>\r\n- Implemented Java Preferences API for persistent data storage<br>\r\n- Utilized JavaFX Scene Graph for smooth UI transitions<br>\r\n- Built a multi-channel audio system with independent volume controls<br>', '2025-02-14', '2025-02-14 23:40:39', '2025-02-23 21:04:13', 'Completed', '2025-02-15', NULL, '[]'),
(5, 'Stock-X E-commerce Template', 'Web Development', 'A modern e-commerce platform built with React, TypeScript, and Tailwind CSS featuring responsive design, authentication system, product management, and comprehensive shopping experience.', '<section class=\"project-header\">\r\nA modern e-commerce template built with React and TypeScript, featuring a sophisticated responsive design, glassmorphism elements, and a comprehensive shopping experience. This project combines professional UI/UX principles with modern web development practices for a complete online shopping platform.\r\n<br><br>\r\n\r\n<h2>Key Features:</h2><br>\r\n\r\n&nbsp â€¢ Authentication System: Elegant login, registration, and password recovery with modern animations<br>\r\n&nbsp â€¢ Product Management: Comprehensive product listing with advanced filtering and sorting options<br>\r\n&nbsp â€¢ Shopping Cart: Full-featured cart management with real-time updates and persistent storage<br>\r\n&nbsp â€¢ Order Processing: Multi-step checkout flow with shipping and payment method selection<br>\r\n&nbsp â€¢ User Dashboard: Account management with order history and tracking functionality<br>\r\n&nbsp â€¢ Wishlist: Personalized product saving system with authentication integration<br>\r\n&nbsp â€¢ Responsive Design: Mobile-first approach ensuring perfect display across all device sizes<br><br>\r\n\r\n<h2>Technical Implementation:</h2><br>\r\n\r\n&nbsp â€¢ Built with React 19.0.0 and TypeScript for type-safe development<br>\r\n&nbsp â€¢ Styled with Tailwind CSS for efficient and consistent styling<br>\r\n&nbsp â€¢ State management using Redux Toolkit for predictable application state<br>\r\n&nbsp â€¢ React Router for client-side routing with lazy-loaded components<br>\r\n&nbsp â€¢ Modern authentication flow with JWT token management<br>\r\n&nbsp â€¢ Responsive layouts with CSS Grid and Flexbox<br>\r\n&nbsp â€¢ Subtle animations using CSS transitions and transforms for enhanced UX<br>\r\n&nbsp â€¢ Deployment configuration with Netlify for continuous integration<br><br>\r\n\r\nThe project demonstrates modern front-end development practices while providing a complete e-commerce solution that can be easily customized and extended for various product categories and business requirements.<br><br>\r\n\r\n<h2>Project Links:</h2><br>\r\n\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Live Demo: <a href=\"https://stock-x-app.netlify.app/\" target=\"_blank\">Stock-X E-commerce</a><br>\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Repository: <a href=\"https://github.com/SallahBoussettah/Stock-X\">GitHub Repository</a><br> \r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Technologies: React, TypeScript, Tailwind CSS, Redux<br>\r\n&nbsp &nbsp &nbsp &nbsp &nbsp â€¢ Version: 1.0.0<br><br>\r\n\r\n<p class=\"date\">A complete e-commerce solution with elegant UI design and modern development practices, showcasing responsive layouts and sophisticated user interactions.</p>\r\n</section>', 'Images/projects/Screenshot 2025-03-06 034337.png', '[\"Images\\/gallery\\/Screenshot 2025-03-05 222325.png\",\"Images\\/gallery\\/Screenshot 2025-03-05 222354.png\",\"Images\\/gallery\\/Screenshot 2025-03-05 222433.png\",\"Images\\/gallery\\/Screenshot 2025-03-05 222452.png\",\"Images\\/gallery\\/Screenshot 2025-03-05 222540.png\"]', 'React 19.0, TypeScript, Redux Toolkit, Tailwind CSS, React Router 7, Framer Motion, React Query, Netlify', '- Creating a responsive design system that works seamlessly across all device sizes<br>\r\n- Implementing a secure and user-friendly authentication flow with proper error handling<br>\r\n- Building an efficient state management architecture for complex product filtering and sorting<br>\r\n- Designing an intuitive checkout process that minimizes abandonment rates<br>\r\n- Optimizing performance with numerous product images and dynamic content loading<br>\r\n- Ensuring accessibility compliance while maintaining visual appeal and modern animations<br>\r\n- Developing a comprehensive but maintainable codebase with proper TypeScript typing', '- Implemented a mobile-first approach with Tailwind CSS breakpoint system for consistent responsive behavior<br>\r\n- Created a modern authentication system with form validation, error handling, and smooth transitions<br>\r\n- Utilized Redux Toolkit with slice pattern for organized global state management and React Query for server state<br>\r\n- Designed multi-step checkout with progress indicators and form state persistence to improve user experience<br>\r\n- Applied lazy loading for images and components with suspense boundaries for optimized rendering<br>\r\n- Built custom UI components with ARIA attributes and keyboard navigation support for accessibility<br>\r\n- Established TypeScript interfaces for all data models to ensure type safety across the application', '2025-03-04', '2025-03-05 22:27:49', '2025-03-27 02:55:44', 'Completed', '2025-03-05', NULL, '[]'),
(6, 'Modern Agency Portfolio Template', 'Web Development', 'A modern and interactive agency portfolio template built with React, TypeScript, and Tailwind CSS featuring smooth animations, responsive design, dark mode support, and comprehensive pages for showcasing creative work and services.', '<section class=\"project-header\">\r\nA sophisticated agency portfolio template designed for creative professionals, featuring elegant animations, responsive layouts, and comprehensive content presentation. This project combines modern UI/UX principles with advanced web development techniques to create an impressive online presence for agencies and freelancers.\r\n<br><br>\r\n\r\n<h2>Key Features:</h2><br>\r\n\r\n&nbsp â€¢ Dynamic Hero Sections: Captivating hero areas with parallax effects and animated text elements<br>\r\n&nbsp â€¢ Portfolio Showcase: Interactive project grid with filtering and detailed project pages<br>\r\n&nbsp â€¢ Services Presentation: Professional service cards with elegant hover effects and detail pages<br>\r\n&nbsp â€¢ Blog System: Complete blog layout with pagination and individual post display<br>\r\n&nbsp â€¢ Dark/Light Mode: Seamless theme switching with persistent user preference storage<br>\r\n&nbsp â€¢ Responsive Design: Mobile-first approach ensuring perfect display across all device sizes<br>\r\n&nbsp â€¢ Contact Form: Interactive contact form with validation and submission handling<br>\r\n&nbsp â€¢ Smooth Animations: Page transitions and micro-interactions using Framer Motion<br><br>\r\n\r\n<h2>Technical Implementation:</h2><br>\r\n\r\n&nbsp â€¢ Built with React 18+ and TypeScript for type-safe, component-based development<br>\r\n&nbsp â€¢ Styled with Tailwind CSS 3.3.5 for efficient and consistent styling<br>\r\n&nbsp â€¢ Animation libraries including Framer Motion and GSAP for sophisticated interactions<br>\r\n&nbsp â€¢ React Router for client-side routing with lazy-loaded components for performance<br>\r\n&nbsp â€¢ React Hook Form for effi